import logo from './logo.svg';
import './App.css';
import React, {useState, useEffect} from "react";
// ES6 import or TypeScript
import { io } from "socket.io-client";
import {ChatMessage} from "./components/ChatMessage";
// CommonJS
const ENDPOINT = "http://127.0.0.1:5000";

function App() {
  const [messages, setMessages] = useState([]);
  const [currMessage, setCurrMessage] = useState('');
  const socket = io(ENDPOINT);
  const user = "MrMeesek"

  const onClick = () => {
    socket.emit("send-message", currMessage, user)
    setCurrMessage("");
  }

  socket.on("receive-message", (message, user) => 
  setMessages([...messages, {user, message}]))

  useEffect(() => {
    socket.on("connect", ()=>console.log("connected"));
  }, []);

  return (
    <div className="App">
      <div style={{display: "flex", 
      background: "#fadadd", justifyContent:"space-around",
      flexDirection: "column", alignItems: "center",
      height: "1200px", width: "1200px"}}>
        <h1>Talkie to ur imaginary frenderson</h1>
        <div style={{height: "1000px", width: "1000px", background: "green", 
      display: "flex",  flexDirection: "column", alignItems: "center"}}>

          <div style={{marginBottom: "10px", background: "white",height: "800px", width: "800px",
        padding: "20px", display: "flex", flexDirection: "column-reverse"}}>
            {messages.map((val, idx) => <ChatMessage key={idx} user={val.user} message={val.message}
            sentByMe={val.user == user}/>)}
          </div>

          <div style={{display: "flex"}}>          
          <textarea value={currMessage} onChange={e=>setCurrMessage(e.target.value)} placeholder="Type a message!" type="textarea"></textarea>
          <button onClick={onClick}>Send</button>

          </div>


        </div>
      </div>
    </div>
  );
}

export default App;
