import {Join} from "./components/Join"
import { BrowserRouter as Router, Route } from "react-router-dom";

// CommonJS
const ENDPOINT = "http://localhost:5000";

function App() {
  return(
  <Router>
  <Route path="/" exact component={Join} />
  <Route path="/chat" component={Chat} />
</Router>
  )
}

export default App;
